ini_Set("display_errors", 1);
// initialize a session
session_start();

// include config file
include 'config.php';


// define local variables for username and password
$username = "";
$password = "";
$username_error = ""; // error message displayed if there is a username error
$password_error = ""; // error message displayed if there is a password error

// loop to enter after login button is pushed
if($_SERVER["REQUEST_METHOD"] == "POST") {
	// Check if username is empty
	if(empty(trim($_POST["username"]))) {
		$username_error = "Username not entered.";
	}
	else {
		$username = trim($_POST["username"]);
	}
	// Check if password is empty
	if(empty(trim($_POST["password"]))) {
		$password_error = "Password not entered.";
	}
	else {
		$password = trim($_POST["password"]);
	}

	// validating username and password
		// creating a SELECT statement
		$sql = "SELECT id, username, password, level FROM users WHERE username = ?";
		if ($stmt = mysqli_prepare($connection, $sql)) {
			// binding the variables to the statement
			mysqli_stmt_bind_param($stmt, "s", $param_username);
			// set the parameters
			$param_username = $username;

			// attempt to execute the sql
			if(mysqli_stmt_execute($stmt)) {
				mysqli_stmt_store_result($stmt);
				// check that username exists in statement
				if (mysqli_stmt_num_rows($stmt) == 1) {
					mysqli_stmt_bind_result($stmt, $id, $username, $saved_password, $userlevel);
					if (mysqli_stmt_fetch($stmt)) {
						// verify password matches
						echo($password);
						echo($saved_password);
						if (strcmp($password, $saved_password) == 0) {
							echo($userlevel);
							// start a new session as a logged in user
							$_SESSION["loggedin"] = true;
							$_SESSION["username"] = $username;
							$_SESSION["id"] = $id;
							$_SESSION["userlevel"] = $userlevel;
							// redirect to appropriate page
							if ($userlevel == '0') {
								header("Location: superadmin.php");
							}
							else if ($userlevel == '1') {
								header("Location: admin.php");
							}
							else {
								header("Location: participant.php");
							}
						}
						// if the password did not match
						else {
							$password_error = "The password that was entered is invalid.";
							}
						}
					}
					// if the username does not exist on file
					else {
						$username_error = "No entry found with the provided username.";
						echo($username_error);
					}
			}
			// if something else went wrong
			else {
				echo "An unexpected error occured.";
			}
		// closing the statement
		mysqli_stmt_close($stmt);
		}
	// closing the connection
	mysqli_close($connection);
}
